import styles from './Description.module.css'

const Description = ({evoChain, multi, child}) => {
    if(multi){
        let paragraph = `<span class="capitalize">${child}</span> evolves to `
        console.log(evoChain)
        for(let i = 0; i < evoChain.length; i++){
            paragraph += `<span class="capitalize>${evoChain[i]}</span>`

            if(i === evoChain.length - 1){
                paragraph += '.'
            }
        }

        return <p dangerouslySetInnerHTML={{ __html: paragraph }}></p>
    }
}

export default Description
