import React, { useEffect, useState } from 'react'
import axios from 'axios'

const Evolution = ({evolution_chain, pokemon_name}) => {
    const [evolution, setEvolution] = useState(null)

    useEffect(() => {
        axios.get(evolution_chain.url)
            .then(res => {
                console.log(res.data)
                setEvolution(res.data)
            })
    }, [])

    if(evolution) {
        const chain = evolution.chain

        const evolvesFrom = chain.species.name === pokemon_name ? null : `${pokemon_name} is the evolved form of ${evolution.chain.species.name}.`
        const evolvesTo = chain.evolves_to[0].species.name ? `${pokemon_name} evolves to ${chain.evolves_to[0].evolves_to[0].species.name}.` : null

        return (
            <div>
                {evolvesFrom}
                {evolvesTo}
            </div>
        )
    } else {
        return null
    }
}

export default Evolution
