import styles from './Description.module.css'

const Description = ({evoChain, multi}) => {
    if(multi){
        const child = evoChain[0]
        let paragraph = `<span class="capitalize">${child}</span> evolves to `

        evoChain.filter(pokemon => {
            if(pokemon === child){
                return false
            }

            return true
        }).forEach(pokemon => {
            paragraph += `<span class="capitalize">${pokemon}</span>,`
        })

        console.log(paragraph)

        return {paragraph}
    }
}

export default Description
