import styles from './Description.module.css'

const Description = ({evoChain, multi}) => {
    if(multi){
        const evoChainMap = evoChain.filter(pokemon => {
            if(pokemon === evoChain[0]){
                return false
            }

            return true
        }).map((pokemon, i) => {
            return (
                <div key={i}>
                    {pokemon}
                </div>
            )
        })

        return <>{evoChainMap}</>
    }
}

export default Description
