const Description = ({evoChain, multi}) => {

    if(multi) {
        let a = evoChain[0].species_name

        const filteredList = evoChain.filter(pokemon => {
            if(pokemon.species_name === a) {
                return false
            }

            return true
        }).map(pokemon => {
            return (
                <div>{`${a} evolves to ${pokemon.species_name}`}</div>
            )
        })
    }

    return  null
}

export default Description
