import Pokemons from '../../assets/data/Pokemons'
import Berries from '../../assets/data/Berries'

const SuggestionBox = ({text, autoComplete}) => {
    const renderSuggestions = () => {
        const suggestions = []

        const filteredSuggestions = Pokemons.filter((data) => {
            if(data.includes(text.toLowerCase())){
                return true
            }
        })

        return filteredSuggestions.map((data, i) => <li key={i} onClick={() => autoComplete(data)}>{data}</li>)
    }

    const render = text ? renderSuggestions() : null


    return (
        <ul>
            {render}
        </ul>
    )
}

export default SuggestionBox
