import React, { useEffect, useState } from 'react'
import axios from 'axios'

const Evolution = ({evolution_chain, pokemon_name}) => {
    const [evolution, setEvolution] = useState(null)

    useEffect(() => {
        axios.get(evolution_chain.url)
            .then(res => {
                console.log(res.data)
                setEvolution(res.data)
            })
    }, [])

    if(evolution) {
        const pokemon = evolution.chain

        const firstEvo = pokemon.species.name 
        const secondEvo = pokemon.evolves_to[0].species.name
        const thirdEvo = pokemon.evolves_to[0].evolves_to[0].species.name 

        const description = `${firstEvo} evolves to ${secondEvo} at level 16. ${secondEvo} evolves to ${thirdEvo} at level`

        return (
            <div>
                {description}
            </div>
        )
    } else {
        return null
    }
}

export default Evolution
