import Link from 'next/link'
import styles from './Description.module.css'

const Description = ({evoChain, multi}) => {

    if(multi) {
        let basic_pokemon = evoChain[0].species_name

        const filteredList = evoChain.filter(pokemon => {
            if(pokemon.species_name === basic_pokemon) {
                return false
            }

            return true
        }).map(pokemon => {
            return (
                <>
                    <Link href={`/pokedex/${basic_pokemon}`}>
                        <span className={styles.cap}>{basic_pokemon}</span>
                    </Link> 
                    {" "}
                    evolves to 
                    {" "}
                    <Link href={`/pokedex/${pokemon.species_name}`}>
                        <span className={styles.cap}>{pokemon.species_name}</span>
                    </Link>
                    .
                    {" "}
                </>
            )
        })

        return (
            <>
                {filteredList}
            </>
        )
    } else if(!multi){

    }
}

export default Description
